import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const Timer = () => {
  const [timerValue, setTimerValue] = useState(0);

  useEffect(() => {
    const socket = io("localhost:3001");
    let timer: NodeJS.Timer = setInterval(() => { }, 0);

    socket.on("timer", (value) => {
      clearInterval(timer);
      setTimerValue(value);
      timer = setInterval(() => {
        setTimerValue((seconds) => seconds + 0.1);
      });
    });

    return () => {
      clearInterval(timer);
      socket.disconnect();
    };
  }, []);

  return <div>Timer: {Math.round(timerValue * 100) / 100}</div>;
};

export default Timer;
